import React from "react";
import { useRecoilState } from "recoil";
import { allTasksState } from "../atoms/TasksAtom";

const useTasks = () => {
  const [tasksStateValue, setTasksStateValue] = useRecoilState(allTasksState);

  return{
    tasksStateValue,
    setTasksStateValue
  }
};
export default useTasks;
